export interface IRequest {
    id: number;
    url: string;
    request: {
        time: Date;
        headers: {
            contentType: string | null;
        }
        body: string | null;
    };
    response?: {
        time: Date;
        statusCode: number
        headers: {
            contentType: string | null;
        }
        body: string;
    };
    appliedRules?: number[];
}

export interface IResponseDetails {
    id: number;
    response: {
        time: Date;
        statusCode: number;
        headers: {
            contentType: string | null;
        }
        body: string | null;
    };
    appliedRules: number[];
}

export interface IAppliedRulesChanged {
    id: number;
    appliedRules: number[];
}
